package com.example.asm_java_nangcao1.Adapter;import android.content.Context;import android.content.SharedPreferences;import android.graphics.Color;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.view.animation.Animation;import android.view.animation.AnimationUtils;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import com.bumptech.glide.Glide;import com.example.asm_java_nangcao1.Model.contact_phone_Model;import com.example.asm_java_nangcao1.quanLyThanhVien_Activity;import com.example.asm_java_nangcao1.Model.accout_user_Model;import com.example.asm_java_nangcao1.R;import java.util.List;public class vertival_listThanhVienLv_Adapter extends BaseAdapter {    private quanLyThanhVien_Activity mycontext;    int mylayout;    List<contact_phone_Model> arrListDanhSach;    public vertival_listThanhVienLv_Adapter(quanLyThanhVien_Activity mycontext, int mylayout, List<contact_phone_Model> arrListDanhSach) {        this.mycontext = mycontext;        this.mylayout = mylayout;        this.arrListDanhSach = arrListDanhSach;    }    @Override    public int getCount() {        return arrListDanhSach.size();    }    @Override    public Object getItem(int i) {        return arrListDanhSach.get(i);    }    @Override    public long getItemId(int i) {        return 0;    }    TextView name, soDienThoai, ngayTaoTaiKhoan, quyen;    @Override    public View getView(int i, View view, ViewGroup viewGroup) {        LayoutInflater inflater = (LayoutInflater) mycontext.getSystemService(Context.LAYOUT_INFLATER_SERVICE);        view = inflater.inflate(mylayout, null);        name = view.findViewById(R.id.itemQuanLyThanhVien_tv_tenNguoiDung);        soDienThoai = view.findViewById(R.id.itemQuanLyThanhVien_tv_soDienThoai);        ngayTaoTaiKhoan = view.findViewById(R.id.itemQuanLyThanhVien_tv_ngayTaoTaiKhoan);        quyen = view.findViewById(R.id.itemQuanLyThanhVien_tv_quyenNguoiDung);        contact_phone_Model arr = arrListDanhSach.get(i);        name.setText("Name: " + arr.getNameUser());        soDienThoai.setText("SĐT: 0" + arr.getNumberPhone());        ngayTaoTaiKhoan.setText("Ngày tạo: " + arr.getNgayTaoTaiKhoan());        if (arr.getQuyen() == 0) {            quyen.setText("Quyền: ADM");        } else {            quyen.setText("Quyền: Người dùng:");        }        soDienThoai.setTextColor(Color.parseColor(arr.getColorText()));        Animation animation = AnimationUtils.loadAnimation(mycontext, R.anim.list_item_anim);        view.startAnimation(animation);        return view;    }}